import sys
from PyQt5 import QtWidgets, QtGui, QtCore
from PyQt5.QtGui import QIcon
from pynput import keyboard
import rtmidi
import base64

class SILAKeyMap(QtWidgets.QWidget):
    def __init__(self):
        super().__init__()
        self.midi_out = None
        self.listener = None
        self.active_keys = set()
        self.mapping = self.generate_key_mapping()
        self.recorded_notes = []
        self.is_recording = False
        self.init_ui()
        self.load_midi_ports()

    def init_ui(self):
        self.setWindowTitle('SILA KeyMap')
        self.setGeometry(100, 100, 800, 600)

        # Cambiar el color de fondo a negro
        self.setStyleSheet("background-color: #000000;")
        
        # Base64 icon string (replace this with your own base64 string)
        icon_base64 = """
        
        """

        icon_data = base64.b64decode(icon_base64)  # Decodificar base64
        icon = QtGui.QPixmap()
        icon.loadFromData(icon_data)  # Cargar el cono desde los datos decodificados
        self.setWindowIcon(QIcon(icon))  # Establecer cono

        main_layout = QtWidgets.QVBoxLayout()

        # Title label
        title_label = self.create_label('SILA KeyMap by Ivan Ayub', font_size=12, font_weight=QtGui.QFont.Bold)
        main_layout.addWidget(title_label)

        # Instructions label
        instructions_label = self.create_label(
            'Usage Instructions:\n'
            '1. Select a MIDI port from the dropdown menu.\n'
            '2. Click "Start Key Capture" to begin capturing.\n'
            '3. Press the keys a-z to send MIDI messages.\n'
            '4. Adjust the controls for the instrument, velocity, and volume.\n'
            '7. Finally, click "Exit" to close the application.',
            font_size=12,
            font_weight=QtGui.QFont.Bold
        )
        main_layout.addWidget(instructions_label)

        # Main controls layout
        control_layout = QtWidgets.QGridLayout()
        control_layout.setSpacing(10)

        # Status
        self.status_label = self.create_label('Status: Inactive', font_size=16)
        control_layout.addWidget(self.status_label, 0, 0, 1, 2)

        # Button to start capturing
        self.capture_button = self.create_button(' Start Capture', self.toggle_capture)
        control_layout.addWidget(self.capture_button, 0, 2)

        # MIDI port selector
        control_layout.addWidget(self.create_label('Select MIDI Port:'), 1, 0)
        self.port_selector = self.create_combobox(self.select_midi_port)
        control_layout.addWidget(self.port_selector, 1, 1, 1, 2)

        # MIDI Instrument
        control_layout.addWidget(self.create_label('MIDI Instrument:'), 2, 0)
        self.instrument_slider = self.create_slider(0, 127, 0)
        control_layout.addWidget(self.instrument_slider, 2, 1, 1, 2)

        # Velocity
        control_layout.addWidget(self.create_label('Velocity:'), 3, 0)
        self.velocity_slider = self.create_slider(0, 127, 64)
        control_layout.addWidget(self.velocity_slider, 3, 1, 1, 2)

        # Master Volume
        control_layout.addWidget(self.create_label('Master Volume:'), 4, 0)
        self.volume_slider = self.create_slider(0, 127, 100)
        control_layout.addWidget(self.volume_slider, 4, 1, 1, 2)

        # Octave
        self.octave_label = self.create_label('Octave: 0', 'octave_label', font_size=16)
        control_layout.addWidget(self.octave_label, 5, 0)
        self.octave_slider = self.create_slider(-2, 2, 0, self.update_octave)
        control_layout.addWidget(self.octave_slider, 5, 1, 1, 2)

        # Active keys visualizer
        self.key_visualizer = self.create_label('Active Keys: None', 'key_visualizer', font_size=14)
        control_layout.addWidget(self.key_visualizer, 7, 0, 1, 3)

        # Note display
        self.note_display = self.create_label('Note: None', 'note_display', font_size=14)
        control_layout.addWidget(self.note_display, 8, 0, 1, 3)

        # Exit button
        control_layout.addWidget(self.create_button(' Exit', self.close), 9, 0, 1, 3)

        main_layout.addLayout(control_layout)
        self.setLayout(main_layout)

    def create_label(self, text, name=None, font_size=14, font_weight=None):
        label = QtWidgets.QLabel(text)
        label.setAlignment(QtCore.Qt.AlignLeft)
        style = f"background-color: #333; border: 2px solid #444; padding: 10px; font-size: {font_size}px; color: #FFD700; "
        if font_weight:
            label.setStyleSheet(style + f" font-weight: {font_weight};")
        else:
            label.setStyleSheet(style)
        if name:
            setattr(self, name, label)
        return label

    def create_button(self, text, callback):
        button = QtWidgets.QPushButton(text)
        button.setStyleSheet("background-color: #444; border: 2px solid #888; padding: 10px; font-size: 14px; border-radius: 5px; color: #FFD700;")
        button.clicked.connect(callback)
        return button

    def create_combobox(self, callback):
        combobox = QtWidgets.QComboBox()
        combobox.setStyleSheet("background-color: #444; border: 2px solid #888; padding: 5px; font-size: 14px; border-radius: 5px; color: #FFD700;")
        combobox.activated.connect(callback)
        return combobox

    def create_slider(self, min_val, max_val, default_val, callback=None):
        slider = QtWidgets.QSlider(QtCore.Qt.Horizontal)
        slider.setMinimum(min_val)
        slider.setMaximum(max_val)
        slider.setValue(default_val)
        slider.setStyleSheet("background-color: #2e2e2e; border: 2px solid #888; padding: 10px; color: #FFD700;")
        if callback:
            slider.valueChanged.connect(callback)
        return slider

    def load_midi_ports(self):
        """Load MIDI ports and display them in the selector."""
        midi_out = rtmidi.MidiOut()
        available_ports = midi_out.get_ports()
        self.port_selector.clear()  # Clear ports before adding
        if available_ports:
            self.port_selector.addItems(available_ports)
            self.midi_out = midi_out.open_port(0)  # Change index if necessary
            self.status_label.setText(f'Connected to {available_ports[0]}')
        else:
            self.status_label.setText('No MIDI ports available')

    def select_midi_port(self):
        """Change the selected MIDI port."""
        selected_port = self.port_selector.currentText()
        if selected_port and self.midi_out is not None:
            self.midi_out.close_port()
            midi_out = rtmidi.MidiOut()
            self.midi_out = midi_out.open_port(self.port_selector.currentIndex())
            self.status_label.setText(f'Connected to {selected_port}')

    def toggle_capture(self):
        """Toggle MIDI capture (start/stop listening)."""
        if self.listener is None:
            self.listener = keyboard.Listener(on_press=self.on_press, on_release=self.on_release)
            self.listener.start()
            self.capture_button.setText(' Stop Capture')
            self.status_label.setText('Status: Capturing keys')
        else:
            self.listener.stop()
            self.listener = None
            self.capture_button.setText(' Start Capture')
            self.status_label.setText('Status: Inactive')

    def update_octave(self):
        """Update the octave label based on the slider value."""
        self.octave_label.setText(f'Octave: {self.octave_slider.value()}')

    def on_press(self, key):
        """Handle key press events."""
        try:
            key_char = key.char
            if key_char in self.mapping:
                note = self.mapping[key_char]
                self.active_keys.add(note)
                self.send_midi_message(note)
                self.note_display.setText(f'Note: {note}')
                if self.is_recording:
                    self.recorded_notes.append(note)
                self.key_visualizer.setText(f'Active Keys: {", ".join(map(str, self.active_keys))}')
        except AttributeError:
            pass  # Ignore special keys

    def on_release(self, key):
        """Handle key release events."""
        try:
            key_char = key.char
            if key_char in self.mapping:
                note = self.mapping[key_char]
                self.active_keys.discard(note)
                self.key_visualizer.setText(f'Active Keys: {", ".join(map(str, self.active_keys))}')
        except AttributeError:
            pass  # Ignore special keys

    def send_midi_message(self, note):
        """Send a MIDI note on message."""
        velocity = self.velocity_slider.value()
        if self.midi_out is not None:
            self.midi_out.send_message([0x90, note, velocity])  # Note on message

    def generate_key_mapping(self):
        """Generate the key mapping based on the specified layout."""
        mapping = {
            'q': 60,  # Example: C4
            'w': 62,  # Example: D4
            'e': 64,  # Example: E4
            'r': 65,  # Example: F4
            't': 67,  # Example: G4
            'y': 69,  # Example: A4
            'u': 71,  # Example: B4
            'i': 72,  # Example: C5
            'o': 74,  # Example: D5
            'p': 76,  # Example: E5
            'a': 48,  # Example: C3
            's': 50,  # Example: D3
            'd': 52,  # Example: E3
            'f': 53,  # Example: F3
            'g': 55,  # Example: G3
            'h': 57,  # Example: A3
            'j': 59,  # Example: B3
            'k': 60,  # Example: C4
            'l': 62,  # Example: D4
            'z': 36,  # Example: C2
            'x': 38,  # Example: D2
            'c': 40,  # Example: E2
            'v': 41,  # Example: F2
            'b': 43,  # Example: G2
            'n': 45,  # Example: A2
            'm': 47,  # Example: B2
        }
        return mapping
    
# Main app initialization
if __name__ == '__main__':
    app = QtWidgets.QApplication(sys.argv)
    window = SILAKeyMap()
    window.show()
    sys.exit(app.exec_())
