import sys
import numpy as np
import sounddevice as sd
import matplotlib.pyplot as plt
from matplotlib.backends.backend_qt5agg import FigureCanvasQTAgg as FigureCanvas
from PyQt5 import QtWidgets, QtCore
from PyQt5.QtGui import QIcon, QPixmap
from PyQt5.QtCore import QByteArray
from scipy.signal import butter, lfilter

class AudioLevelPlotter(QtWidgets.QWidget):
    """Clase para graficar los niveles de audio."""

    def __init__(self):
        super().__init__()
        self.fig, self.ax = plt.subplots()
        self.canvas = FigureCanvas(self.fig)
        layout = QtWidgets.QVBoxLayout(self)
        layout.addWidget(self.canvas)
        self.setLayout(layout)

        # Configuraciones iniciales
        self.ax.set_ylim(-1, 1)  # Amplitud entre -1 y 1
        self.ax.set_xlim(0, 1)  # Tiempo inicial
        self.ax.fill_between([], [], color='red', alpha=0.5)  # Área roja

    def plot_audio_level(self, time, amplitude):
        """Actualizar la gráfica con los niveles de audio."""
        self.ax.cla()  # Limpiar el eje para nuevas gráficas
        self.ax.set_ylim(-1, 1)
        self.ax.set_xlim(0, time[-1])
        self.ax.fill_between(time, amplitude, color='red', alpha=0.5)  # Área roja
        self.canvas.draw()

def butter_lowpass(cutoff, fs, order=5):
    """Diseñar un filtro Butterworth de paso bajo."""
    nyq = 0.5 * fs
    normal_cutoff = cutoff / nyq
    b, a = butter(order, normal_cutoff, btype='low', analog=False)
    return b, a

def lowpass_filter(data, cutoff, fs, order=5):
    """Aplicar un filtro Butterworth de paso bajo a los datos."""
    b, a = butter_lowpass(cutoff, fs, order=order)
    return lfilter(b, a, data)

class SILAJackBay(QtWidgets.QWidget):
    """Interfaz principal de SILAJackBay."""

    def __init__(self):
        super().__init__()

        # Base64 icono (debes reemplazar el string a continuación por el código base64 real de tu ícono)
        icon_base64 = b''

        # Crear el ícono desde base64
        pixmap = QPixmap()
        pixmap.loadFromData(QByteArray.fromBase64(icon_base64))
        self.setWindowIcon(QIcon(pixmap))

        self.setWindowTitle("SILA JackBay")
        self.setStyleSheet("background-color: #2a2a2a; color: yellow;")
        self.is_muted = False
        self.history = []

        # Diseño de la interfaz
        layout = QtWidgets.QHBoxLayout(self)

        # Panel lateral izquierdo
        left_panel = QtWidgets.QWidget()
        left_layout = QtWidgets.QVBoxLayout(left_panel)
        left_panel.setStyleSheet("background-color: #333333;")

        # Dispositivos de entrada
        self.input_label = QtWidgets.QLabel("Input devices:")
        self.input_combo = QtWidgets.QComboBox()
        left_layout.addWidget(self.input_label)
        left_layout.addWidget(self.input_combo)

        # Dispositivos de salida
        self.output_label = QtWidgets.QLabel("Output Devices:")
        self.output_combo = QtWidgets.QComboBox()
        left_layout.addWidget(self.output_label)
        left_layout.addWidget(self.output_combo)

        # Controles de volumen y balance
        self.volume_label = QtWidgets.QLabel("Volume:")
        self.volume_slider = QtWidgets.QSlider(QtCore.Qt.Horizontal)
        self.balance_label = QtWidgets.QLabel("Balance:")
        self.balance_slider = QtWidgets.QSlider(QtCore.Qt.Horizontal)

        left_layout.addWidget(self.volume_label)
        left_layout.addWidget(self.volume_slider)
        left_layout.addWidget(self.balance_label)
        left_layout.addWidget(self.balance_slider)

        # Botones de control
        self.mute_button = QtWidgets.QPushButton("Mute")
        self.mute_button.clicked.connect(self.toggle_mute)
        self.test_button = QtWidgets.QPushButton("Test Audio")
        self.test_button.clicked.connect(self.test_audio)

        left_layout.addWidget(self.mute_button)
        left_layout.addWidget(self.test_button)

        # Panel lateral derecho
        right_panel = QtWidgets.QWidget()
        right_layout = QtWidgets.QVBoxLayout(right_panel)
        right_panel.setStyleSheet("background-color: #444444;")

        # Historial de configuraciones
        self.history_label = QtWidgets.QLabel("Configuration History:")
        self.history_list = QtWidgets.QListWidget()
        self.history_list.itemDoubleClicked.connect(self.apply_history_setting)

        right_layout.addWidget(self.history_label)
        right_layout.addWidget(self.history_list)

        # Botón para guardar configuraciones
        self.save_button = QtWidgets.QPushButton("Save Settings")
        self.save_button.clicked.connect(self.save_current_setting)

        right_layout.addWidget(self.save_button)

        # Área central para la gráfica
        self.plot_area = AudioLevelPlotter()

        # Añadir paneles y área central al layout principal
        layout.addWidget(left_panel)
        layout.addWidget(self.plot_area)
        layout.addWidget(right_panel)

        # Ajustar proporciones
        layout.setStretch(0, 1)
        layout.setStretch(1, 2)
        layout.setStretch(2, 1)

        self.populate_input_devices()  # Llenar dispositivos de entrada
        self.populate_output_devices()  # Llenar dispositivos de salida
        self.start_audio_stream()  # Iniciar captura de audio

    def closeEvent(self, event):
        """Detener el flujo de audio y cerrar la aplicación."""
        if hasattr(self, 'stream'):
            self.stream.stop()
            self.stream.close()
        event.accept() 

    def populate_input_devices(self):
        """Llenar el combo de dispositivos de entrada."""
        input_devices = sd.query_devices()
        for idx, device in enumerate(input_devices):
            if device['max_input_channels'] > 0:  # Asegurarse de que tenga canales de entrada
                self.input_combo.addItem(device['name'], userData=idx)

    def populate_output_devices(self):
        """Llenar el combo de dispositivos de salida."""
        output_devices = sd.query_devices()
        for idx, device in enumerate(output_devices):
            if device['max_output_channels'] > 0:  # Asegurarse de que tenga canales de salida
                self.output_combo.addItem(device['name'], userData=idx)

    def save_current_setting(self):
        """Guardar la configuración actual en el historial."""
        entry = {
            'input_device': self.input_combo.currentText(),
            'output_device': self.output_combo.currentText(),
            'volume': self.volume_slider.value(),
            'balance': self.balance_slider.value()
        }
        self.history.append(entry)
        self.history_list.addItem(f"Input: {entry['input_device']} | Output: {entry['output_device']} | Volume: {entry['volume']} | Balance: {entry['balance']}")

    def apply_history_setting(self, item):
        """Aplicar una configuración seleccionada del historial."""
        index = self.history_list.row(item)
        setting = self.history[index]

        self.input_combo.setCurrentText(setting['input_device'])
        self.output_combo.setCurrentText(setting['output_device'])
        self.volume_slider.setValue(setting['volume'])
        self.balance_slider.setValue(setting['balance'])

    def toggle_mute(self):
        """Alternar el estado de silencio."""
        self.is_muted = not self.is_muted
        self.mute_button.setText("Reactivate" if self.is_muted else "Mute")

    def test_audio(self):
        """Probar el audio generando una señal de prueba."""
        duration = 1.0  # 1 segundo
        fs = 44100  # Frecuencia de muestreo
        f = 440.0  # Frecuencia de la señal
        t = np.linspace(0, duration, int(fs * duration), endpoint=False)
        signal = 0.5 * np.sin(2 * np.pi * f * t)
        sd.play(signal, samplerate=fs)

    def start_audio_stream(self):
        """Iniciar la captura de audio para graficar en tiempo real."""
        def callback(indata, frames, time, status):
            amplitude = indata[:, 0]
            time_values = np.linspace(0, len(amplitude)/44100, len(amplitude))
            self.plot_area.plot_audio_level(time_values, amplitude)

        self.stream = sd.InputStream(callback=callback)
        self.stream.start()

if __name__ == "__main__":
    app = QtWidgets.QApplication(sys.argv)
    window = SILAJackBay()
    window.show()
    sys.exit(app.exec_())
